package net.ACCUWeather.WeatherSubtypes;public class Temperature {	short temperature;	short maxTemperature=-999;		public Temperature(String value) {			this.temperature=Short.valueOf(value);					}		public Temperature(String low,String max) {		this.temperature=Short.valueOf(low);		this.maxTemperature=Short.valueOf(max);			}	@Override	public String toString() { 		if(maxTemperature!=-999)			return temperature+UnitCode.temperature()+"/"+maxTemperature+UnitCode.temperature();		return temperature+""+UnitCode.temperature();	}			public boolean equals(Temperature t) {		return temperature==t.temperature&&maxTemperature==t.maxTemperature;		}	}